angular.module('myApp',[]).controller('modalController',function($scope){	$scope.showModal = false;	$scope.toggleModal = function(){				$scope.buttonClicked = 'buttonClicked';				$scope.showModal = !$scope.showModal;				}}).directive('genericModal', function () {    return {      template: '<div class="modal fade">' +           '<div class="modal-dialog">' +             '<div class="modal-content">' +               '<div style="background-color:rgba(14, 14, 14, 0.31);" class="modal-header">' +                 '<h4 style="background-color:rgba(14, 14, 14, 0.31);" class="modal-title">{{ buttonClicked }} </h4>' +               '</div>' +               '<div style="background-color:rgba(121, 119, 119, 0.31);" class="modal-body" ng-transclude/>' + 			  '<div class="modal-body" style="width:100%;float:right;background-color:white" >' +			  '<button style="margin:5px;" type="button" class="btn btn-default btn-sm pull-right" data-dismiss="modal" aria-hidden="true">back</button>'+ '</div>'+            '</div>' +           '</div>' +         '</div>',      restrict: 'E',      transclude: true,      replace:true,      scope:true,      link: function postLink(scope, element, attrs) {          scope.$watch(attrs.visible, function(value){          if(value == true)            $(element).modal('show');          else            $(element).modal('hide');        });        $(element).on('shown.bs.modal', function(){          scope.$apply(function(){            scope.$parent[attrs.visible] = true;          });        });        $(element).on('hidden.bs.modal', function(){          scope.$apply(function(){            scope.$parent[attrs.visible] = false;          });        });      }    };  });